mapper.select:
select

    distinct


from t_agent_info




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

SELECT tai.agentId, tai.agentId, tai.agentName, tai.status, tai.createTime, tai.userName, tai.realName, tai.qq,
tai.tel, tai.email, tai.address, taa.balance FROM t_agent_info tai
INNER JOIN t_agent_account taa
ON tai.agentId = taa.agentId
WHERE 1=1
AND tai.parentAgentId = #{parentAgentId}


        limit ${offset}, ${limit}


        limit ${limit}
