mapper.select:
select

    distinct


from t_mch_info




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

SELECT count(0) FROM ( SELECT t.MchId, t.PassageId FROM t_pay_order t WHERE t.STATUS IN (2, 3)
AND t.MchId = #{mchId}
AND t.PassageId = #{passageId}
AND t.PaySuccTime >= #{createTimeStart}
AND t.PaySuccTime <= #{createTimeEnd}
GROUP BY t.MchId, t.PassageId ) bcc

SELECT count(0) FROM ( SELECT t.MchId, t.ProductId FROM t_pay_order t WHERE t.STATUS IN (3)
AND t.MchId = #{mchId}
AND t.ProductId = #{productId}
AND t.PaySuccTime >= #{createTimeStart}
AND t.PaySuccTime <= #{createTimeEnd}
GROUP BY t.MchId, t.ProductId ) bcc
