mapper.select:
select

  distinct


from t_sett_record




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

SELECT SUM(SettAmount) AS totalSettAmount,
SUM(SettFee) AS totalSettFee,
COUNT(1) AS totalCount
FROM t_sett_record
WHERE SettStatus IN (5)
 AND CreateTime >= #{createTimeStart} 
 AND CreateTime <= #{createTimeEnd} 
;

SELECT
SUM(SettAmount) AS totalAmount,
SUM(SettFee) AS totalFee,
COUNT(1) AS totalCount
FROM t_sett_record
WHERE 1 = 1
 AND InfoId = #{infoId} 
 AND SettOrderId = #{settOrderId} 
 AND AccountName like #{accountName} 
 AND SettStatus = #{settStatus} 
 AND CreateTime >= #{createTimeStart} 
 AND CreateTime <= #{createTimeEnd} ;
