mapper.select:
select

  distinct


from t_trans_order




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

SELECT
SUM(Amount) AS totalAmount,
COUNT(1) AS totalCount
FROM t_trans_order
WHERE 1 = 1
 AND mchId = #{mchId} 
 AND TransOrderId = #{transOrderId} 
 AND MchTransNo = #{mchTransNo} 
 AND CreateTime >= #{createTimeStart} 
 AND CreateTime <= #{createTimeEnd} ;

SELECT
SUM(Amount) AS totalAmount,
SUM(ChannelCost) AS totalChannelCost,
COUNT(1) AS totalCount
FROM t_trans_order
WHERE Result = 2
 AND mchId = #{mchId} 
 AND TransOrderId = #{transOrderId} 
 AND MchTransNo = #{mchTransNo} 
 AND CreateTime >= #{createTimeStart} 
 AND CreateTime <= #{createTimeEnd} ;
