mapper.select:
select

  distinct


from t_pay_passage




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

select id,PassageName,PassageRate,twoDaysRecharge,oneDaysRecharge,currentDaysRecharge,ordersNumber,MchIncome, successOrdersNumber,FORMAT((successOrdersNumber/ordersNumber*100),2) as successRate  from t_pay_passage
left join (select PassageId,sum(Amount) as currentDaysRecharge , count(0) as successOrdersNumber ,sum(MchIncome) as MchIncome from t_pay_order where `status` in(3)
and CreateTime  >  "${createTimeStart}" and CreateTime  <   "${createTimeEnd}"
GROUP BY PassageId) acc on acc.PassageId=t_pay_passage.id


left join (select PassageId, count(0) as ordersNumber from t_pay_order    where
CreateTime  >  "${createTimeStart}" and CreateTime   <  "${createTimeEnd}"  GROUP BY PassageId
) bcc  on bcc.PassageId=t_pay_passage.id

left join (select PassageId,sum(Amount) as twoDaysRecharge from t_pay_order  where
CreateTime  >  "${twoDaysDate} 00:00:00" and CreateTime   <  "${twoDaysDate} 23:59:59" and
`status` in(3)    GROUP BY PassageId
)ccc on   ccc.PassageId=t_pay_passage.id

left join (select PassageId,sum(Amount) as oneDaysRecharge  from t_pay_order   where
CreateTime  >  "${oneDaysDate} 00:00:00" and CreateTime   <    "${oneDaysDate} 23:59:59" and
`status` in(3)    GROUP BY PassageId
)dcc on   dcc.PassageId=t_pay_passage.id

  where id =#{id}

order by currentDaysRecharge desc


    limit ${offset}, ${limit}


    limit ${limit}
